function applyTheme(e){document.body.classList.remove("theme-auto","theme-light","theme-dark"),document.body.classList.add(`theme-${e}`)}document.addEventListener("DOMContentLoaded",()=>{const a=localStorage.getItem("theme")||"auto";applyTheme(a),document.querySelectorAll(".site-list__input-theme").forEach(e=>{let t=e;t.checked=a===t.value,t.addEventListener("change",e=>{localStorage.setItem("theme",e.target.value),applyTheme(e.target.value)})})});